# === Pydantic 模型定义 ===
class Sentences(BaseModel):
    sentences: List[str]

class ChunkMeta(BaseModel):
    chunk_id: str
    title: str
    summary: str
    propositions: List[str]

class ChunkID(BaseModel):
    chunk_id: str

